import React, { useState } from "react";
import axios from "axios";

function App() {
	const [inputChange, setInputChange] = useState({
		phone: "",
		msg: "",
	});

	const onChange = (e, key) => {
		setInputChange({ [key]: e.target.value });
		console.log(inputChange);
	};

	const sendMsg = async () => {
		let msgSend = await axios.post(
			"https://www.apiorange.com/api/send/sms.do",
			{
				sender: "024230616",
				phone: inputChange.phone,
				msg: inputChange.msg,
			},
			{
				headers: {
					Authorization: "hP0w+Z5Ng59lXNFL9CZtYw==",
					"Content-Type": "application/json; charset=utf-8",
				},
			}
		);
		console.log(msgSend);
	};

	return (
		<div className="App">
			<p>발송자 번호</p>
			<input
				type="text"
				placeholder="발송번호를 입력해주세요."
				onChange={(e) => onChange(e, "phone")}
			/>
			<p>메세지</p>
			<textarea
				style={{ resize: "none" }}
				onChange={(e) => onChange(e, "msg")}
			/>
			<br />
			<button onClick={sendMsg}>문자 보내기</button>
		</div>
	);
}

export default App;
